var loopback = require('./');

var Product = loopback.createModel('Product', {
  name: 'string'
});

var memory = loopback.memory();
Product.attachTo(memory);

Product.create({
  name: 'one'
}, function(err, data) {
  if (err) throw err;
  data.name = 'two';
  Product.updateOrCreate(data, function(err, res) {
    if (err) throw err;
    res._initProperties({name: 'three' });
    console.log('result', res);
    console.log('__data', res.__data);
    console.log('JSON', res.toJSON());
  });
});
      
