var fs = require('fs');
var loopback = require('./');
var _ = require('underscore.string');

var allModels = loopback.modelBuilder.models;
Object.keys(allModels).forEach(function(modelName) {
  if (/^Anonymous/.test(modelName)) return;
  if (modelName === 'Model') return;

  var fileName = 'common/models/' + _.dasherize(modelName).slice(1) + '.json';
  console.log(modelName, '=>', fileName);
  console.log(allModels[modelName].definition);
});
