var loopback = require('./');

var Product = loopback.createModel('Product', {
  name: 'string',
  xname: 'string'
});

Product.getter['xname'] = function() {
    return this.__data.name + "x"
};

var memory = loopback.memory();
Product.attachTo(memory);

var p = new Product({ name: 'one' });
console.log('name', p.name);
console.log('xname', p.xname);
